import React, { useState } from 'react';
import styled from 'styled-components';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import PhoneFrame from './components/PhoneFrame';
import AuthenticationView from './components/AuthenticationView';
import CapsuleListView from './components/CapsuleListView';
import NewCapsuleView from './components/NewCapsuleView';
import CapsuleDetailView from './components/CapsuleDetailView';

const AppContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  display: flex;
  justify-content: center;
  align-items: center;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
`;

const PreviewLabel = styled.div`
  position: absolute;
  top: 20px;
  left: 50%;
  transform: translateX(-50%);
  background: rgba(0, 0, 0, 0.8);
  color: white;
  padding: 10px 20px;
  border-radius: 20px;
  font-size: 14px;
  z-index: 1000;
`;

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [currentView, setCurrentView] = useState('intro');

  const navigate = (view) => {
    setCurrentView(view);
  };

  const renderCurrentView = () => {
    switch (currentView) {
      case 'auth':
        return <AuthenticationView onAuthenticate={() => {
          setIsAuthenticated(true);
          navigate('capsuleList');
        }} />;
      case 'capsuleList':
        return <CapsuleListView onNavigate={navigate} />;
      case 'newCapsule':
        return <NewCapsuleView onNavigate={navigate} />;
      case 'capsuleDetail':
        return <CapsuleDetailView onNavigate={navigate} />;
      default:
        return <AuthenticationView onAuthenticate={() => {
          setIsAuthenticated(true);
          navigate('capsuleList');
        }} />;
    }
  };

  return (
    <AppContainer>
      <PreviewLabel>
        ðŸŽ¬ Time Capsule Camera - Live Preview
      </PreviewLabel>
      <PhoneFrame>
        {renderCurrentView()}
      </PhoneFrame>
    </AppContainer>
  );
}

export default App;
