import React, { useState } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';

const Container = styled.div`
  height: 100%;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  display: flex;
  flex-direction: column;
  overflow: hidden;
`;

const Header = styled.div`
  padding: 20px;
  text-align: center;
`;

const Title = styled.h2`
  color: white;
  font-size: 24px;
  font-weight: 700;
  margin: 0 0 8px 0;
`;

const Subtitle = styled.p`
  color: rgba(255, 255, 255, 0.8);
  font-size: 16px;
  margin: 0;
`;

const CardContainer = styled.div`
  flex: 1;
  padding: 0 20px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const TutorialCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.95);
  border-radius: 20px;
  padding: 30px;
  text-align: center;
  max-width: 280px;
  width: 100%;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.2);
`;

const StepNumber = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 20px;
  background: #4c63d2;
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 700;
  margin: 0 auto 20px auto;
`;

const StepIcon = styled.div`
  font-size: 48px;
  margin-bottom: 20px;
`;

const StepTitle = styled.h3`
  color: #333;
  font-size: 20px;
  font-weight: 700;
  margin: 0 0 12px 0;
`;

const StepDescription = styled.p`
  color: #666;
  font-size: 16px;
  line-height: 1.4;
  margin: 0;
`;

const ExampleVideo = styled.div`
  width: 100%;
  height: 120px;
  background: #000;
  border-radius: 12px;
  margin: 16px 0;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 24px;
  position: relative;
  overflow: hidden;
`;

const PlayButton = styled.div`
  position: absolute;
  width: 40px;
  height: 40px;
  border-radius: 20px;
  background: rgba(255, 255, 255, 0.9);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 16px;
  color: #333;
`;

const FakeTimeline = styled.div`
  display: flex;
  gap: 4px;
  margin: 12px 0;
  justify-content: center;
`;

const TimelineClip = styled.div`
  width: 20px;
  height: 12px;
  background: ${props => props.color || '#4c63d2'};
  border-radius: 2px;
`;

const Navigation = styled.div`
  padding: 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const DotsIndicator = styled.div`
  display: flex;
  gap: 8px;
`;

const Dot = styled.div`
  width: 8px;
  height: 8px;
  border-radius: 4px;
  background: ${props => props.active ? 'white' : 'rgba(255, 255, 255, 0.3)'};
  transition: all 0.3s ease;
`;

const NavButton = styled.button`
  background: rgba(255, 255, 255, 0.2);
  border: none;
  color: white;
  padding: 12px 20px;
  border-radius: 20px;
  font-weight: 600;
  cursor: pointer;

  &:hover {
    background: rgba(255, 255, 255, 0.3);
  }

  &.primary {
    background: rgba(255, 255, 255, 0.9);
    color: #4c63d2;

    &:hover {
      background: white;
    }
  }
`;

const tutorialSteps = [
  {
    icon: "üìπ",
    title: "Record moments",
    description: "Capture videos with friends throughout your day, week, or month",
    demo: "recording"
  },
  {
    icon: "üîí",
    title: "Seal your capsule",
    description: "Set a future unlock date. Videos stay locked until then‚Äîno peeking!",
    demo: "sealed"
  },
  {
    icon: "üë•",
    title: "Everyone contributes",
    description: "Friends add their own videos to the same capsule",
    demo: "contributors"
  },
  {
    icon: "‚è∞",
    title: "Time reveals the story",
    description: "When unsealed, all videos play chronologically as one epic home movie",
    demo: "playback"
  }
];

const TutorialView = ({ onComplete }) => {
  const [currentStep, setCurrentStep] = useState(0);

  const nextStep = () => {
    if (currentStep < tutorialSteps.length - 1) {
      setCurrentStep(currentStep + 1);
    } else {
      onComplete();
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const renderDemo = (type) => {
    switch (type) {
      case 'recording':
        return (
          <ExampleVideo>
            üì±
            <PlayButton>‚ñ∂Ô∏è</PlayButton>
          </ExampleVideo>
        );
      case 'sealed':
        return (
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '32px', margin: '16px 0' }}>üîí</div>
            <div style={{ color: '#FFD700', fontSize: '18px', fontWeight: 'bold' }}>
              127 days left
            </div>
          </div>
        );
      case 'contributors':
        return (
          <div>
            <div style={{ display: 'flex', justifyContent: 'center', gap: '8px', margin: '16px 0' }}>
              <div style={{ width: '32px', height: '32px', borderRadius: '16px', background: '#FF6B6B', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>üë©</div>
              <div style={{ width: '32px', height: '32px', borderRadius: '16px', background: '#4ECDC4', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>üë®</div>
              <div style={{ width: '32px', height: '32px', borderRadius: '16px', background: '#45B7D1', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>üòä</div>
            </div>
            <FakeTimeline>
              <TimelineClip color="#FF6B6B" />
              <TimelineClip color="#4ECDC4" />
              <TimelineClip color="#FF6B6B" />
              <TimelineClip color="#45B7D1" />
              <TimelineClip color="#4ECDC4" />
            </FakeTimeline>
          </div>
        );
      case 'playback':
        return (
          <ExampleVideo>
            üé¨
            <PlayButton>‚ñ∂Ô∏è</PlayButton>
            <div style={{ 
              position: 'absolute', 
              bottom: '8px', 
              left: '8px', 
              fontSize: '10px', 
              background: 'rgba(0,0,0,0.7)', 
              padding: '2px 6px', 
              borderRadius: '4px' 
            }}>
              Sarah ‚Ä¢ 2:14 PM
            </div>
          </ExampleVideo>
        );
      default:
        return null;
    }
  };

  const step = tutorialSteps[currentStep];

  return (
    <Container>
      <Header>
        <Title>How it works</Title>
        <Subtitle>Create memories that unlock over time</Subtitle>
      </Header>

      <CardContainer>
        <AnimatePresence mode="wait">
          <TutorialCard
            key={currentStep}
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: -50 }}
            transition={{ duration: 0.3 }}
          >
            <StepNumber>{currentStep + 1}</StepNumber>
            <StepIcon>{step.icon}</StepIcon>
            <StepTitle>{step.title}</StepTitle>
            <StepDescription>{step.description}</StepDescription>
            {renderDemo(step.demo)}
          </TutorialCard>
        </AnimatePresence>
      </CardContainer>

      <Navigation>
        <NavButton 
          onClick={prevStep}
          style={{ opacity: currentStep === 0 ? 0.5 : 1 }}
          disabled={currentStep === 0}
        >
          Previous
        </NavButton>

        <DotsIndicator>
          {tutorialSteps.map((_, index) => (
            <Dot key={index} active={index === currentStep} />
          ))}
        </DotsIndicator>

        <NavButton 
          className="primary"
          onClick={nextStep}
        >
          {currentStep === tutorialSteps.length - 1 ? 'Get Started' : 'Next'}
        </NavButton>
      </Navigation>
    </Container>
  );
};

export default TutorialView;
