import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const Container = styled.div`
  height: 100%;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  overflow-y: auto;
`;

const TopBar = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 16px 20px;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
`;

const LogoButton = styled.button`
  background: none;
  border: none;
  font-size: 20px;
  cursor: pointer;
  opacity: 0.8;

  &:hover {
    opacity: 1;
  }
`;

const CreateButton = styled.button`
  background: rgba(255, 255, 255, 0.9);
  border: none;
  color: #4c63d2;
  padding: 8px 16px;
  border-radius: 20px;
  font-weight: 600;
  cursor: pointer;

  &:hover {
    background: white;
  }
`;

const Section = styled.div`
  margin-bottom: 24px;
`;

const SectionTitle = styled.h3`
  color: white;
  font-size: 18px;
  font-weight: 700;
  margin: 0 0 16px 20px;
`;

const ActiveCapsulesRow = styled.div`
  display: flex;
  gap: 16px;
  padding: 0 20px;
  overflow-x: auto;

  &::-webkit-scrollbar {
    display: none;
  }
`;

const ActiveCapsuleCard = styled(motion.div)`
  min-width: 280px;
  background: rgba(255, 255, 255, 0.15);
  border-radius: 16px;
  padding: 20px;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  cursor: pointer;
`;

const CapsuleCover = styled.div`
  width: 100%;
  height: 120px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  margin-bottom: 16px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 32px;
  background-image: ${props => props.cover ? `url(${props.cover})` : 'none'};
  background-size: cover;
  background-position: center;
`;

const CapsuleTitle = styled.h4`
  color: white;
  font-size: 16px;
  font-weight: 600;
  margin: 0 0 8px 0;
`;

const CountdownDisplay = styled.div`
  color: #FFD700;
  font-size: 24px;
  font-weight: 800;
  margin-bottom: 12px;
  text-align: center;
`;

const CountdownLabel = styled.div`
  color: rgba(255, 255, 255, 0.8);
  font-size: 12px;
  text-align: center;
  margin-bottom: 16px;
`;

const ParticipantFaces = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  margin-bottom: 12px;
`;

const Face = styled.div`
  width: 28px;
  height: 28px;
  border-radius: 14px;
  background: rgba(255, 255, 255, 0.2);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 14px;
  border: 2px solid rgba(255, 255, 255, 0.3);
`;

const MoreFaces = styled.div`
  color: rgba(255, 255, 255, 0.7);
  font-size: 12px;
  font-weight: 600;
`;

const QuickRecordButton = styled.button`
  width: 100%;
  background: rgba(255, 69, 58, 0.9);
  border: none;
  color: white;
  padding: 12px;
  border-radius: 8px;
  font-weight: 600;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;

  &:hover {
    background: rgba(255, 69, 58, 1);
  }
`;

const UnlockedGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 16px;
  padding: 0 20px;
`;

const UnlockedCard = styled(motion.div)`
  aspect-ratio: 1;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 16px;
  padding: 16px;
  cursor: pointer;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const UnlockedCover = styled.div`
  flex: 1;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 24px;
  margin-bottom: 12px;
`;

const UnlockedTitle = styled.h5`
  color: white;
  font-size: 14px;
  font-weight: 600;
  margin: 0;
`;

