const CapsuleDashboard = ({ onNavigate }) => {
  const [countdowns, setCountdowns] = useState({});

  useEffect(() => {
    const updateCountdowns = () => {
      const newCountdowns = {};
      activeCapsules.forEach(capsule => {
        const now = new Date();
        const diff = capsule.unlockDate - now;

        if (diff > 0) {
          const days = Math.floor(diff / (1000 * 60 * 60 * 24));
          const hours = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
          const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));

          if (days > 0) {
            newCountdowns[capsule.id] = { value: days, unit: days === 1 ? 'day' : 'days' };
          } else if (hours > 0) {
            newCountdowns[capsule.id] = { value: hours, unit: hours === 1 ? 'hour' : 'hours' };
          } else {
            newCountdowns[capsule.id] = { value: minutes, unit: minutes === 1 ? 'minute' : 'minutes' };
          }
        } else {
          newCountdowns[capsule.id] = { value: 'UNLOCKED!', unit: '' };
        }
      });
      setCountdowns(newCountdowns);
    };

    updateCountdowns();
    const interval = setInterval(updateCountdowns, 60000); // Update every minute

    return () => clearInterval(interval);
  }, []);

  return (
    <Container>
      <TopBar>
        <LogoButton>üé¨</LogoButton>
        <CreateButton onClick={() => onNavigate('newCapsule')}>
          + Create
        </CreateButton>
      </TopBar>

      {activeCapsules.length > 0 ? (
        <Section>
          <SectionTitle>Active Capsules</SectionTitle>
          <ActiveCapsulesRow>
            {activeCapsules.map((capsule, index) => (
              <ActiveCapsuleCard
                key={capsule.id}
                onClick={() => onNavigate('capsuleDetail')}
                initial={{ opacity: 0, x: 50 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: index * 0.1 }}
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
              >
                <CapsuleCover cover={capsule.cover}>
                  üé•
                </CapsuleCover>

                <CapsuleTitle>{capsule.title}</CapsuleTitle>

                <CountdownDisplay>
                  {countdowns[capsule.id]?.value || '...'}
                </CountdownDisplay>
                <CountdownLabel>
                  {countdowns[capsule.id]?.unit || 'loading...'}
                </CountdownLabel>

                <ParticipantFaces>
                  {capsule.participants.slice(0, 5).map((face, i) => (
                    <Face key={i}>{face}</Face>
                  ))}
                  {capsule.participants.length > 5 && (
                    <MoreFaces>+{capsule.participants.length - 5}</MoreFaces>
                  )}
                </ParticipantFaces>

                <QuickRecordButton onClick={(e) => {
                  e.stopPropagation();
                  onNavigate('record');
                }}>
                  ‚è∫Ô∏è Quick Record
                </QuickRecordButton>
              </ActiveCapsuleCard>
            ))}
          </ActiveCapsulesRow>
        </Section>
      ) : (
        <EmptyState>
          <EmptyCard>
            <EmptyTitle>Make your first capsule</EmptyTitle>
            <EmptySubtitle>Shoot it. Seal it. Share it later.</EmptySubtitle>
            <CreateButton onClick={() => onNavigate('newCapsule')}>
              Create Capsule
            </CreateButton>
          </EmptyCard>
        </EmptyState>
      )}

      {unlockedCapsules.length > 0 && (
        <Section>
          <SectionTitle>Unlocked Capsules</SectionTitle>
          <UnlockedGrid>
            {unlockedCapsules.map((capsule, index) => (
              <UnlockedCard
                key={capsule.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.05 }}
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
              >
                <UnlockedCover>{capsule.cover}</UnlockedCover>
                <div>
                  <UnlockedTitle>{capsule.title}</UnlockedTitle>
                  <UnlockedDate>{capsule.unlockedDate}</UnlockedDate>
                </div>
              </UnlockedCard>
            ))}
          </UnlockedGrid>
        </Section>
      )}

      <FAB
        onClick={() => onNavigate('record')}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        animate={{ 
          boxShadow: [
            "0 8px 32px rgba(255, 59, 48, 0.4)",
            "0 8px 32px rgba(255, 59, 48, 0.6)",
            "0 8px 32px rgba(255, 59, 48, 0.4)"
          ]
        }}
        transition={{ duration: 2, repeat: Infinity }}
      >
        ‚è∫Ô∏è
