const RecordingScreen = ({ onNavigate, capsuleName = "Summer Road Trip" }) => {
  const [isRecording, setIsRecording] = useState(false);
  const [vhsMode, setVhsMode] = useState(true);
  const [flash, setFlash] = useState(false);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [recordingProgress, setRecordingProgress] = useState(0);
  const [showSaveNotification, setShowSaveNotification] = useState(false);
  const [aspectRatio, setAspectRatio] = useState('9:16');

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    let interval;
    if (isRecording) {
      interval = setInterval(() => {
        setRecordingProgress(prev => {
          if (prev >= 100) {
            setIsRecording(false);
            handleSave();
            return 0;
          }
          return prev + 1;
        });
      }, 100);
    } else {
      setRecordingProgress(0);
    }

    return () => clearInterval(interval);
  }, [isRecording]);

  const formatTimestamp = (date) => {
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    const year = date.getFullYear();
    const hours = String(date.getHours()).padStart(2, '0');
    const minutes = String(date.getMinutes()).padStart(2, '0');
    const seconds = String(date.getSeconds()).padStart(2, '0');

    return `${month}/${day}/${year} ‚Äì ${hours}:${minutes}:${seconds}`;
  };

  const handleShutter = () => {
    if (!isRecording) {
      setIsRecording(true);
    } else {
      setIsRecording(false);
      handleSave();
    }
  };

  const handleSave = () => {
    setShowSaveNotification(true);
    setTimeout(() => {
      setShowSaveNotification(false);
    }, 2000);
  };

  const circumference = 2 * Math.PI * 38;
  const strokeDasharray = circumference;
  const strokeDashoffset = circumference - (recordingProgress / 100) * circumference;

  return (
    <Container>
      <BackButton onClick={() => onNavigate('capsuleList')}>
        ‚Üê
      </BackButton>

      <CameraView vhsMode={vhsMode}>
        üìπ
        <VHSNoise vhsMode={vhsMode} />
      </CameraView>

      <TopOverlays>
        <LocationOverlay>
          Ridgewood, NJ ‚Äì {new Date().toLocaleDateString()}
        </LocationOverlay>
        <RecordingIndicator
          animate={isRecording ? { scale: [1, 1.05, 1] } : {}}
          transition={{ duration: 0.5, repeat: isRecording ? Infinity : 0 }}
        >
          <RecDot recording={isRecording} />
          REC
        </RecordingIndicator>
      </TopOverlays>

      <CapsuleSelector>
        <CapsuleAvatars>
          <Avatar>üë©</Avatar>
          <Avatar>üë®</Avatar>
          <Avatar>üòä</Avatar>
        </CapsuleAvatars>
        <CapsuleName>{capsuleName}</CapsuleName>
      </CapsuleSelector>

      <LeftToolbar>
        <ToolbarButton
          className={vhsMode ? 'active' : ''}
          onClick={() => setVhsMode(!vhsMode)}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          üìº
        </ToolbarButton>
        <ToolbarButton
          className={flash ? 'active' : ''}
          onClick={() => setFlash(!flash)}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          ‚ö°
        </ToolbarButton>
        <ToolbarButton
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          ‚è±Ô∏è
        </ToolbarButton>
      </LeftToolbar>

      <RightToolbar>
        <ToolbarButton
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          üîÑ
        </ToolbarButton>
        <ToolbarButton
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          üì±
        </ToolbarButton>
        <ToolbarButton
          onClick={() => {
            const ratios = ['9:16', '1:1', '16:9'];
            const currentIndex = ratios.indexOf(aspectRatio);
            setAspectRatio(ratios[(currentIndex + 1) % ratios.length]);
          }}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          {aspectRatio === '9:16' ? 'üì±' : aspectRatio === '1:1' ? '‚¨ú' : 'üì∫'}
        </ToolbarButton>
      </RightToolbar>

      <BottomControls>
        <ToolbarButton
          onClick={() => onNavigate('upload')}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          üìÅ
        </ToolbarButton>

        <ShutterButton
          recording={isRecording}
          onClick={handleShutter}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          {isRecording && (
            <ProgressRing>
              <circle
                cx="44"
                cy="44"
                r="38"
                stroke="rgba(255,255,255,0.3)"
                strokeWidth="3"
                fill="none"
              />
              <circle
                cx="44"
                cy="44"
                r="38"
                stroke="#FF3B30"
                strokeWidth="3"
                fill="none"
                strokeDasharray={strokeDasharray}
                strokeDashoffset={strokeDashoffset}
                style={{ transition: 'stroke-dashoffset 0.1s linear' }}
              />
            </ProgressRing>
          )}
        </ShutterButton>

        <ToolbarButton
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          ‚öôÔ∏è
        </ToolbarButton>
      </BottomControls>

      <BottomOverlays>
        <TimestampOverlay>
          {formatTimestamp(currentTime)}
        </TimestampOverlay>
        <RecorderNameOverlay>
          Mike Y.
        </RecorderNameOverlay>
      </BottomOverlays>

      {showSaveNotification && (
        <SaveNotification
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -20 }}
        >
          ‚úÖ Saved to {capsuleName}
        </SaveNotification>
      )}
    </Container>
  );
};

